package com.github.alexvoliveira.plugin.core.spigot.common.enums;

import com.bnstra.npclib.api.skin.Skin;
import lombok.Getter;
import lombok.RequiredArgsConstructor;

@Getter
@RequiredArgsConstructor
public enum SkinsNpcEnums {

    SKYWARS("", ""),
    BEDWARS("", ""),
    EVENTS("","");

    private final String value;
    private final String signature;
    private final Skin skin;

    SkinsNpcEnums(String value, String signature) {
        this.value = value;
        this.signature = signature;
        this.skin = new Skin(value, signature);
    }
}
